# -*- coding: utf-8 -*-import loggingfrom features.steps.config import log_levelif log_level not in ('debug', 'info', 'warning', 'error', 'critical'):    log_level = 'info'level_name = {'debug': logging.DEBUG, 'info': logging.INFO, 'warning': logging.WARNING, 'error': logging.ERROR,              'critical': logging.CRITICAL}logging.basicConfig(filename='running.log',                    filemode='w',  # 模式w就是写模式，每次都会重新写日志，覆盖之前的日志，a是追加模式，默认如果不写的话，就是追加模式                    level=level_name[log_level],                    format='%(asctime)s - %(pathname)s : %(lineno)d lines - %(levelname)s - %(message)s',                    datefmt='%Y-%m-%d %H:%M:%S')logger = logging.getLogger()